// Game.js
import React from 'react';
import '../sass/game.scss';
import TriangleIcon from '../images/bg-triangle.svg'; // Adjust path as necessary
import PaperComponent from '../components/PaperComponent';
import ScissorsComponent from '../components/ScissorsComponent';
import RockComponent from '../components/RockComponent';
import { GameProvider } from '../context/GameContext';

const Game = () => {
  return (
    <GameProvider>
      <div className="game-container">
        <div className="game">
          <div className="triangle">
            <img src={TriangleIcon} alt="triangle" />
            <div className="choices-row-one">
              <PaperComponent />
              <ScissorsComponent />
              <RockComponent />
            </div>
          </div>
        </div>
      </div>
    </GameProvider>
  );
};

export default Game;
