import React, { useContext } from 'react';
import Paper from '../images/icon-paper.svg';
import '../sass/paper.scss';
import { GameContext } from '../context/GameContext';

const PaperComponent = () => {
  const { selectedChoice, setSelectedChoice } = useContext(GameContext);

  const handleClick = () => {
    setSelectedChoice('Paper');
  };

  return (
    <div className="paper-container" onClick={handleClick} style={{ display: selectedChoice === 'Paper' ? 'none' : 'block' }}>
      <div className="hand">
        <img src={Paper} alt="paper" />
      </div>
    </div>
  );
};

export default PaperComponent;
