import React, { useEffect, useContext, useState } from 'react';
import '../sass/game.scss';
import Triangle from '../images/bg-triangle.svg';
import PaperComponent from '../components/paper';
import ScissorsComponent from '../components/scissors';
import RockComponent from '../components/rock';
import { GameContext } from '../context/GameContext';

const Game = () => {
  const { hideTriangle, setHideTriangle } = useContext(GameContext);
  const { setSelectedChoice } = useContext(GameContext);
  const [chosenComponent, setChosenComponent] = useState(null);
  const [computerChoice, setComputerChoice] = useState(null);
  const [isComputerChoosing, setIsComputerChoosing] = useState(false);

  const handleChoiceClick = (choice) => {
    setSelectedChoice(choice);
    setChosenComponent(choice);

    // Simulate computer's choice after player selects
    setHideTriangle(true);
    setIsComputerChoosing(true);
    setTimeout(() => {
      const randomNumber = getRandomInt(3);
      setComputerChoice(randomNumber);
      setIsComputerChoosing(false);
    }, 3000); // Adjust delay as needed
  };

  const hideTriangleStyles = {
    display: hideTriangle ? 'none' : 'block',
  };

  const renderChosenComponent = () => {
    switch (chosenComponent) {
      case 'Paper':
        return <PaperComponent />;
      case 'Scissors':
        return <ScissorsComponent />;
      case 'Rock':
        return <RockComponent />;
      default:
        return null;
    }
  };

  const getRandomInt = (max) => {
    return Math.floor(Math.random() * max);
  };

  const renderComputerChoice = () => {
    if (computerChoice === 0) {
      return <PaperComponent />;
    } else if (computerChoice === 1) {
      return <ScissorsComponent />;
    } else if (computerChoice === 2) {
      return <RockComponent />;
    } else {
      return null;
    }
  };

  return (
    <div className="game-container">
      <div className="game">
        <div className="triangle" style={hideTriangleStyles}>
          <img src={Triangle} alt="triangle" />
          <div className="choices-row-one">
            <div className="paper-container" onClick={() => handleChoiceClick('Paper')}>
              <PaperComponent />
            </div>
            <div className="scissors-container" onClick={() => handleChoiceClick('Scissors')}>
              <ScissorsComponent />
            </div>
          </div>
          <div className="choice-row-two">
            <div className="rock-container" onClick={() => handleChoiceClick('Rock')}>
              <RockComponent />
            </div>
          </div>
        </div>
        <div className="result">
          {chosenComponent && <div className="chosen-component">{renderChosenComponent()}</div>}
          {isComputerChoosing && <div className='computer-choice'><h1>Computer is choosing...</h1></div>}
          {!isComputerChoosing && computerChoice !== null && (
            <div className='computer-choice'>{renderComputerChoice()}</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Game;
