import React, { createContext, useState } from 'react';

const GameContext = createContext();

const GameProvider = ({ children }) => {
  const [selectedChoice, setSelectedChoice] = useState(null);
  const [pickSide, setPickSide] = useState(false);
  const [hideTriangle, setHideTriangle] = useState(false);
  const [computerSide, setComputerSide] = useState(false);
  const [wins, setWins] = useState(0); // State for wins
  const [losses, setLosses] = useState(0); // State for losses

  // Function to update score based on game result
  const updateScore = (result) => {
    if (result === 'win') {
      setWins((prevWins) => prevWins + 1);
    } else if (result === 'lose') {
      setLosses((prevLosses) => prevLosses + 1);
    }
    // Ties do not affect the score
  };

  return (
    <GameContext.Provider value={{ 
      selectedChoice, 
      setSelectedChoice, 
      pickSide, 
      setPickSide, 
      hideTriangle, 
      setHideTriangle, 
      computerSide, 
      setComputerSide,
      wins,
      losses,
      updateScore
    }}>
      {children}
    </GameContext.Provider>
  );
};

export { GameProvider, GameContext };
